import React, { useState } from "react";
import "./countApp.css"

function Counter(props)
{

    let [countValue, setCountValue] = useState(1);


return(
    <div className="count">
        <h1 className="title"> Counter component</h1>
        <h3> Count value before button click = {++countValue}</h3>
        <button onClick={ ()=> setCountValue(10)}>Count Button</button>
        <h3> Count value after button click: {countValue++}</h3>

        <hr></hr>
        </div> 
);

}

export default Counter;
