import React, { useState } from "react";

function Counter(props)
{

    let [countValue, setCountValue] = useState(1);


return(
    <div>
        <h1> Counter component</h1>
        <h2> child state value before button click: {++countValue}</h2>
        <button onClick={ ()=> setCountValue(10)}>Count Button</button>
        <h2> child state value after button click: {countValue++}</h2>
        <h2> child state value after button click: {countValue}</h2>
        <hr></hr>
        </div> 
);

}

export default Counter;
